        QRCodeWriter qrCodeWriter = new QRCodeWriter();

        String msg = "https://www.ifsc.edu.br";
        BitMatrix bitMatrix = qrCodeWriter.encode(msg, BarcodeFormat.QR_CODE, 600, 600);

        Path archive = FileSystems.getDefault().getPath("qrcode.png");

        MatrixToImageWriter.writeToPath(bitMatrix, "PNG", archive);
